import React from 'react';


export class ToggleInfo extends React.Component{
  constructor(props){
    super(props);
    this.state={
      hideContent:false,
      moreIcon: this.props.moreIcon,
      lessIcon: this.props.lessIcon,
      iconToShow: ''
    }
    this.handleClick = this.handleClick.bind(this);
  }
  componentDidMount(){
    this.setState({
      iconToShow:this.state.moreIcon
    });
  }
  handleClick(){
    this.setState({
      hideContent: !this.state.hideContent,
      iconToShow: this.state.lessIcon
    });
  }


  render(){
    return(
      <div className="toggleinfo-main">
      <div className = "toggleinfo-titleIcon" onClick={this.handleClick}>
          <span><h1>{this.props.title}</h1></span>
          <span><i className={this.state.iconToShow}></i></span>
        </div>
        {this.state.hideContent &&
          <div className="toggleinfo-content">
            {this.props.content}
          </div>}
      </div>
    );
  }

}
export default ToggleInfo;
