import React from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import { MDBRow, MDBContainer, MDBInput, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import { MDBDropdown, MDBDropdownToggle, MDBDropdownMenu, MDBDropdownItem } from "mdbreact";

import moment from 'moment';
import DayPickerInput from 'react-day-picker/DayPickerInput.js';
import { formatDate, parseDate } from 'react-day-picker/moment';
import 'react-day-picker/lib/style.css';

export class Filter extends React.Component{
  constructor(props){
    super(props);

    this.state={
        arr: [],
      from: undefined,
      to: undefined,
      startD: '',
      endD: '',
      spaceTypes:[
        {
          value:'Restaurant space',
          label:'Restaurant'
        },
        {
          value:'Mall space',
          label:'Mall'
        },
        {
          value:'Apartment space',
          label:'Apartment'
        },
        {
          value:'Garage space',
          label:'Garage'
        },
        {
          value:'Closet space',
          label:'Closet'
        },
        {
          value:'Basement space',
          label:'Basement'
        },
        {
          value:'Outdoor space',
          label:'Outdoor (i.e shed)'
        }
      ]
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleFromChange = this.handleFromChange.bind(this);
    this.handleToChange = this.handleToChange.bind(this);
    this.get2D = this.get2D.bind(this);
    this.formatDates = this.formatDates.bind(this);
  }

  get2D( num ) {
     if( num.toString().length < 2 ) // Integer of less than two digits
         return "0" + num; // Prepend a zero!
     return num.toString(); // return string for consistency
  }
  formatDates(date){
    const months=['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    date.split(' '); //split by spaceTypes
    let year = date[3].toString();
    let day = date[2].toString();
    let month = months.indexOf(date[1])+1;
    const monthString = this.get2D(month);
    const newDate = year + '-' + month + '-' + day;
    console.log("new date" + newDate);
    return newDate;
  }

  showFromMonth() {
    const { from, to } = this.state;
    if (!from) {
      return;
    }
    if (moment(to).diff(moment(from), 'months') < 2) {
      this.to.getDayPicker().showMonth(from);
    }
  }
  handleFromChange(from) {
    // Change the from date and focus the "to" input field
    console.log(from);
    const d = this.formatDates(from);
    this.setState({
      from,
      startD:d
     });
  }
  handleToChange(to) {
    const d = this.formatDates(to);
    this.setState({
      to,
      endD:d

     }, this.showFromMonth);
  }
  handleChange(event) {
console.log("hi");
