if(!exists){
   this.setState({
  arr: this.state.arr.concat(event.target.value)
});
}else{
  var array = [...this.state.arr];
  var index = array.indexOf(event.target.value)
  if (index !== -1) {
    array.splice(index, 1);
    this.setState({arr: array});
  }

}
//call filter function
 }


  render(){
    const { from, to } = this.state;
    const modifiers = { start: from, end: to };
    const spaceTypeDropItems = this.state.spaceTypes.map((space, index) =>
        <Dropdown.Item value={space.value}>{space.label}</Dropdown.Item>


    );
    return(
      <div className="filter-main">
        <div className="filter-spaceTypeDropdown">
        <label>
          Pick your favorite flavor:
          <select value={this.state.arr} multiple={true} onChange={this.handleChange}>
            <option value="grapefruit">Grapefruit</option>
            <option value="lime">Lime</option>
            <option value="coconut">Coconut</option>
            <option value="mango">Mango</option>
          </select>
        </label>
        </div>
        <div className="InputFromTo">
           <DayPickerInput
             value={from}
             placeholder="From"
             format="LL"
             formatDate={formatDate}
             parseDate={parseDate}
             dayPickerProps={{
               selectedDays: [from, { from, to }],
               disabledDays: { after: to },
               toMonth: to,
               modifiers,
               numberOfMonths: 1,
               onDayClick: () => this.to.getInput().focus(),
             }}
             onDayChange={this.handleFromChange}
           />{' '}
           â€”{' '}
           <span className="InputFromTo-to">
             <DayPickerInput
               ref={el => (this.to = el)}
               value={to}
               placeholder="To"
               format="LL"
               formatDate={formatDate}
               parseDate={parseDate}
               dayPickerProps={{
                 selectedDays: [from, { from, to }],
                 disabledDays: { before: from },
                 modifiers,
                 month: from,
                 fromMonth: from,
                 numberOfMonths: 1,
               }}
               onDayChange={this.handleToChange}
             />
           </span>
        </div>

      </div>

    );
  }

}

export default Filter;
