import React, { Component } from 'react';
import 'mdbreact/dist/css/mdb.css'
//import logo from './logo.svg';
import '../App.css';
import '../Styles/Listing.css';
import Table from './Table.js';
import Map from './Map.js';
import MainNav from './MainNav.js';
import HowItWorks from './HowItWorks.js';
import Filter from './Filter.js';

import { MDBRow, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import { BrowserRouter, Route, NavLink } from 'react-router-dom';
import Fullpage, { FullPageSections, FullpageSection } from 'react-fullpage';

import moment from 'moment';

import * as Api from '../apiActions.js';
import * as API from "../apiActions";

// import Main from './Main.js';
// import PracticeNewPage from './Components/PracticeNewPage.js';
// import {
//   Route,
//   NavLink,
//   HashRouter
// } from "react-router-dom";
export class Listing extends Component{
  constructor(props){
    super(props);
    this.state={
      showModal:false,
      login:false,
      signup:false,
      searchVal:this.props.location.state,
      data: [],
      arr: [],
      from: undefined,
      to: undefined,
      startD: '',
      endD: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleFromChange = this.handleFromChange.bind(this);
    this.handleToChange = this.handleToChange.bind(this);
    this.get2D = this.get2D.bind(this);
    this.formatDates = this.formatDates.bind(this);
  }
//put fitlers in brackets as from_plade_id: "string", from_radis:"8"
  componentDidMount(){
    Api.Listings.all({},
      data=>{
        console.log(data);
        this.setState({
          data:data
        });
      }
    );
  }

  searchForListings(placeId, radius, transportationMode, startDate, endDate, spaceType){
      API.Listings.all({
          from_place_id: placeId,
          radius: radius,
          space_type: spaceType,
          start_date: startDate,
          end_date: endDate,
          transportation_mode: transportationMode
      }, response => {
          console.log("Search Query: " + placeId)
          console.log("Search Results:\n")
          console.log(response)
          this.setState({
              data: response
          })
      })
  }


    get2D( num ) {
       if( num.toString().length < 2 ) // Integer of less than two digits
           return "0" + num; // Prepend a zero!
       return num.toString(); // return string for consistency
    }

    formatDates(date){
      const months=['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
      var dateA = date.toString().split(' '); //split by spaceTypes
      let year = dateA[3].toString();
      let day = dateA[2].toString();
      let month = months.indexOf(dateA[1])+1;
      const monthString = this.get2D(month);
      const newDate = year + '-' + month + '-' + day;
      console.log("new date" + newDate);
      return newDate;
    }

    showFromMonth() {
      const { from, to } = this.state;
      if (!from) {
        return;
      }
      if (moment(to).diff(moment(from), 'months') < 2) {
        this.to.getDayPicker().showMonth(from);
      }
    }

    handleFromChange(from) {
      // Change the from date and focus the "to" input field
      console.log(from);
      const d = this.formatDates(from);
      this.setState({
        from,
        startD:d
       });
    }

    handleToChange(to) {
      const d = this.formatDates(to);
      this.setState({
        to,
        endD:d

       }, this.showFromMonth);
    }

    handleChange(event) {
      console.log("hi");
      const exists = this.state.arr.some(v => (v === event.target.value));
      if(!exists){
         this.setState({
        arr: this.state.arr.concat(event.target.value)
      });
      }else{
        var array = [...this.state.arr];
        var index = array.indexOf(event.target.value)
        if (index !== -1) {
          array.splice(index, 1);
          this.setState({arr: array});
        }

      }
  //call filter function
   }


    render() {


      const hosts = [
  			{
  				firstName: 'Scott',
  				lastName: 'McConnell',
  				street: '1262 Farm Rd',
  				city: 'Berwyn',
  				state: 'Pennsylvania',
  				zip: '19312',
  				email: 'skm44@duke.edu',
  				phone: '6103124662',
  				spaceType: 'Garage',
  				spaceAvailable: '160',
  				createdAt: new Date()
  			},
  			{
  				firstName: 'Josh',
  				lastName: 'France',
  				street: '220 Alexander Ave',
  				city: 'Durham',
  				state: 'North Carolina',
  				zip: '27705',
  				email: 'jrf36@duke.edu',
  				phone: '9802146075',
  				spaceType: 'Shed',
  				spaceAvailable: '100',
  				createdAt: new Date(),
  			},
  			{
  				firstName: 'Samir',
  				lastName: 'Agadi',
  				street: '207 Erwin Rd',
  				city: 'Durham',
  				state: 'North Carolina',
  				zip: '27705',
  				email: 'sa280@duke.edu',
  				phone: '2486335250',
  				spaceType: 'Closet',
  				spaceAvailable: '50',
  				createdAt: new Date(),
  			},
  		];

    // handleNavModalOption = event =>{
    //   console.log("nav option clicked");
    //   switch(event.target.id){
    //     case 'login':
    //       this.setState({
    //         login:true
    //         //signup:false;
    //       });
    //       break;
    //     case 'signup':
    //       this.setState({
    //         login:true
    //         //signup:false
    //       });
    //       break;
    //   }
    // };
    // toggleNavModal = () => {
    //   this.setState({ showModal: !this.state.showModal });
    // };
//{this.state.showModal && <In currUser = {this.state.login} handleCancel = {this.toggleNavModal}/>}
    return (

      <div className="listing-main">

          <MainNav searchVal={this.state.searchVal} showSearch={true}/>
          <MDBRow>
            <Filter arr={this.state.arr} handleToChange = {this.handleToChange} handleFromChange={this.handleFromChange}  handleChange = {this.handleChange}/>
          </MDBRow>

        <div className ="listing-content">
          <MDBRow className="listing-row">
            <MDBCol size="sm">
              <Table dataPoints={this.state.data}/>
            </MDBCol>
            <MDBCol size="sm">
              <Map data={this.state.data} onPlaceSearched={(placeId) => this.searchForListings(placeId, 1200, 'driving', this.state.startD, this.state.startD, null)}/>
            </MDBCol>
          </MDBRow>

        </div>
      </div>






    );
  }

}
export default Listing;
