import React from 'react';
import { BrowserRouter, Route, NavLink } from 'react-router-dom'

import HostForm1 from './HostForm1.js';
import HostForm2 from './HostForm2.js';
import HostForm3 from './HostForm3.js';
import HostForm4 from './HostForm4.js';
import ProgressNav from './ProgressNav.js';
import Button from 'react-bootstrap/Button';
import ProgressBar from 'react-bootstrap/ProgressBar';

export class HostForm extends React.Component{
  constructor(props){
    super(props);
    this.state={
      PageNumber:1,
      FormComponent:<HostForm1/>,
      //FormComponent: "",
      disable:false
    };
    this.handleBack=this.handleBack.bind(this);
    this.handleNext=this.handleNext.bind(this);
  }

  handleBack(){

    if(this.state.PageNumber>1){
      this.setState({
        PageNumber: this.state.PageNumber-1, //if back one more is out of bounds
        FormComponent: "HostForm1"+this.state.PageNumber,
        //FormComponent:this.state.FormComponent
      });
    }
  }
  handleNext(){
    if(this.state.PageNumber<4){
      this.setState({
        PageNumber: this.state.PageNumber+1, //if back one more is out of bounds
        FormComponent: "HostForm1"+this.state.PageNumber,
        //FormComponent:this.state.FormComponent
      });
    }
  }
  render(){
    const FormComponent = this.state.FormComponent;
    var MyComponent = this.state.FormComponent;

    return(

      <div className="HostForm">
        <div className="HostForm-header">
          <ProgressBar />
        </div>

            <div className="content">

              <Route exact path="/BecomeAHost1" component={HostForm1}/>
              <Route path="/BecomeAHost2" component={HostForm2}/>
            </div>

            <div className = "buttons">
            {this.state.PageNumber>1 && <Button type = "submit" value="Submit" variant="outline-primary" onClick={this.handleBack}></Button> }
            {this.state.PageNumber<4 && <Button type = "submit" value="Submit" variant="outline-primary" onClick={this.handleBack}><NavLink to="/BecomeAHost1">Back</NavLink></Button> }
            </div>
          </div>



    );
  }
}
export default HostForm;
