import React from 'react';


export class ToggleInfo extends React.Component{
  constructor(props){
    super(props);
    this.state={
      hideContent:false
    }
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(){
    this.setState({
      hideContent: !this.state.hideContent
    });
  }


  render(){
    return(
      <div className="toggleinfo-main">
        <div className = "toggleinfo-titleIcon" onClick={this.handleClick}>
          <h1>{this.props.title}</h1>
          <span><i className={this.props.icon}></i></span>
        </div>

        {this.state.hideContent &&
          <div className="toggleinfo-content">
            {this.props.content}
          </div>
          }

      </div>
    );
  }

}
export default ToggleInfo;
