import React from 'react';
import MainNav from './MainNav.js';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBIcon, MDBInput } from 'mdbreact';
import Footer from './Footer.js';
import '../Styles/ContactPage.css';

export class ContactPage extends React.Component{
  constructor(props){
    super(props);
    this.state={
      name:'',
      email:'',
      subject:'',
      messsage:'',
      nameClass:'',
      emailClass:'',
      subjectClass:'',
      messsageClass:''

    }
    this.handleSubmit=this.handleSubmit.bind(this);
  }

  handleSubmit(e){
    e.preventDefault();
    console.log("hi");
    const {name, email, subject, message} = this.state;
    let flag = true;
    var emailClass='';
    var nameClass='';
    var subjectClass='';
    var messageClass='';
    if(email===''){
      console.log("email no");
      flag=false;
      emailClass="invalid";
    }
    if(name===''){
      console.log("name no");
      flag=false;
      nameClass="invalid";
    }
    if(subject===''){
      console.log("no subject");
      flag=false;
      subjectClass="invalid";
    }
    if(message===''){
      console.log("no message");
      flag=false;
      messageClass="invalid";
    }
    if(flag){
      console.log('you can submit!');
    }else{
      console.log("there were errors!");
      this.setState({
        emailClass,
        phoneClass,
        startDClass,
        endDClass
      })
    }


  }
  render(){
    return(
      <div className="contactpage-main">
        <MainNav showSearch={true}/>
        <MDBContainer id="contactpage-container">
          <MDBRow id="title">
              <h1>Contact Us</h1>
          </MDBRow>

          <div className="contactpage-content">
          <MDBRow id="form">
            <form
              onSubmit={this.handleSubmit}
              noValidate
            >
              <p className="h5 text-center mb-4">Questions? Comments? Concerns?</p>
              <div className="grey-text">
                <MDBInput
                  label="Your name *"
                  group
                  type="text"
                  validate
                  error="wrong"
                  success="right"
                  required
                />
                <MDBInput
                  label="Your email *"
                  group
                  type="email"
                  validate
                  error="wrong"
                  success="right"
                  required
                />
                <MDBInput
                  label="Subject *"
                  group
                  type="text"
                  validate
                  error="wrong"
                  success="right"
                  required
                />
                <MDBInput
                  type="textarea"
                  rows="2"
                  label="Your message *"
                  required
                />
              </div>
              <div className ="contactpage-submit">
                <MDBBtn outline color="secondary" type="submit">
                  Send
                </MDBBtn>
              </div>

            </form>
          </MDBRow>
        </div>
        </MDBContainer>

        <footer>
          <Footer/>
        </footer>
    </div>
    );
  }
}
export default ContactPage;
