import React from 'react';
import { MDBRow, MDBContainer, MDBInput, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import "../../index.css";



export class CountItem extends React.Component{
  constructor(props){
    super(props);
    this.state={
      count:0
    };
    this.handleIncrement = this.handleIncrement.bind(this);
    this.handleDecrement = this.handleDecrement.bind(this);
  }

  handleIncrement(){
    console.log("up");

    this.setState({
      count:this.state.count+1
    });
  }
  handleDecrement(){
    console.log("down");
    this.setState({
      count:this.state.count==0? 0:this.state.count-1
    });
  }
  render(){
    return(
      <div>
      <MDBRow>
        <MDBCol>
          {this.props.name}
        </MDBCol>
        <MDBCol>
          <div className="def-number-input number-input">
           <button onClick={this.handleDecrement} className="minus"></button>
           <input className="quantity" name="quantity" value={this.state.count} onChange={()=> console.log('change')}
           type="number" />
           <button onClick={this.handleIncrement} className="plus"></button>
         </div>
        </MDBCol>
      </MDBRow>
      </div>

    );
  }
}
export default CountItem;
