  render(){
    return(
      <div classname="popup-main">
      <MDBContainer>

      <MDBContainer>
        <MDBModal isOpen={this.props.show} toggle={()=>this.props.handleToggle()} size="md">
          <MDBModalBody>
            <MDBRow>
              <MDBCol>
                <img src = {this.props.member.picSrc}/>
              </MDBCol>

              <MDBCol>
                <MDBRow>
                  <p>{this.props.member.name}</p>
                </MDBRow>
                <MDBRow>
                  <p>{this.props.member.bio}</p>
                </MDBRow>
              </MDBCol>

            </MDBRow>
          </MDBModalBody>

        </MDBModal>
      </MDBContainer>
      </MDBContainer>
      </div>

    );
  }
}
export default Popup;
