import React from 'react';

import { MDBRow, MDBContainer, MDBInput, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import { MDBDropdown, MDBDropdownToggle, MDBDropdownMenu, MDBDropdownItem } from "mdbreact";

import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import ListItemText from '@material-ui/core/ListItemText';
import Select from '@material-ui/core/Select';
import Checkbox from '@material-ui/core/Checkbox';
import Chip from '@material-ui/core/Chip';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';

import moment from 'moment';
import DayPickerInput from 'react-day-picker/DayPickerInput.js';
import { formatDate, parseDate } from 'react-day-picker/moment';
import 'react-day-picker/lib/style.css';


export class Filter extends React.Component{
  constructor(props){
    super(props);
    this.state={
      from: undefined,
      to: undefined,
      colors:[],
      name: [],
      spaceTypes:[
        {
          value:'Restaurant space',
          label:'Restaurant'
        },
        {
          value:'Mall space',
          label:'Mall'
        },
        {
          value:'Apartment space',
          label:'Apartment'
        },
        {
          value:'Garage space',
          label:'Garage'
        },
        {
          value:'Closet space',
          label:'Closet'
        },
        {
          value:'Basement space',
          label:'Basement'
        },
        {
          value:'Outdoor space',
          label:'Outdoor (i.e shed)'
        }
      ]
    }
  }

  showFromMonth() {
    const { from, to } = this.state;
    if (!from) {
      return;
    }
    if (moment(to).diff(moment(from), 'months') < 2) {
      this.to.getDayPicker().showMonth(from);
    }
  }
  handleFromChange(from) {
    // Change the from date and focus the "to" input field
    this.setState({ from });
  }
  handleToChange(to) {
    this.setState({ to }, this.showFromMonth);
  }



 handleChangeMultiple = event => {
   const { options } = event.target;
   const value = [];
   for (let i = 0, l = options.length; i < l; i += 1) {
     if (options[i].selected) {
       value.push(options[i].value);
     }
   }
   this.setState({
     name: value,
   });
 };

  render(){
    const names = [
  'Oliver Hansen',
  'Van Henry',
  'April Tucker',
  'Ralph Hubbard',
  'Omar Alexander',
  'Carlos Abbott',
  'Miriam Wagner',
  'Bradley Wilkerson',
  'Virginia Andrews',
  'Kelly Snyder',
];


    const { from, to } = this.state;
    const modifiers = { start: from, end: to };
    const spaceTypeDropItems = this.state.spaceTypes.map((space, index) =>
        <MDBDropdownItem value={space.value} >{space.label}</MDBDropdownItem>


    );
    const options = [
  { value: 'chocolate', label: 'Chocolate' },
  { value: 'strawberry', label: 'Strawberry' },
  { value: 'vanilla', label: 'Vanilla' }
];
const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
    maxWidth: 300,
  },
  chips: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  chip: {
    margin: theme.spacing.unit / 4,
  },
  noLabel: {
    marginTop: theme.spacing.unit * 3,
  },
});

    return(
      <div className="filter-main">
        <div className="filter-spaceTypeDropdown">
        <FormControl >
          <InputLabel htmlFor="select-multiple-chip">Chip</InputLabel>
          <Select
            multiple
            value={this.state.name}
            onChange={this.handleChange}
            input={<Input id="select-multiple-chip" />}
            renderValue={selected => (
              <div >
                {selected.map(value => (
                  <Chip key={value} label={value}  />
                ))}
              </div>
            )}
            MenuProps={MenuProps}
          >
            {names.map(name => (
              <MenuItem key={name} value={name}>
                {name}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        </div>
        <div className="InputFromTo">
           <DayPickerInput
             value={from}
             placeholder="From"
             format="LL"
             formatDate={formatDate}
             parseDate={parseDate}
             dayPickerProps={{
               selectedDays: [from, { from, to }],
               disabledDays: { after: to },
               toMonth: to,
               modifiers,
               numberOfMonths: 1,
               onDayClick: () => this.to.getInput().focus(),
             }}
             onDayChange={this.handleFromChange}
           />{' '}
           â€”{' '}
           <span className="InputFromTo-to">
             <DayPickerInput
               ref={el => (this.to = el)}
               value={to}
               placeholder="To"
               format="LL"
               formatDate={formatDate}
               parseDate={parseDate}
               dayPickerProps={{
                 selectedDays: [from, { from, to }],
                 disabledDays: { before: from },
                 modifiers,
                 month: from,
                 fromMonth: from,
                 numberOfMonths: 1,
               }}
               onDayChange={this.handleToChange}
             />
           </span>
        </div>

      </div>

    );
  }

}

export default Filter;
