import React from 'react';
import AvatarEditor from 'react-avatar-editor';
import Dropzone from 'react-dropzone';
import { MDBView, MDBMask } from "mdbreact";

export class DropFile extends React.Component{
  constructor(props){
    super(props);
  }

  handleMouseOver(){
    console.log("mousing over the picture");
  }
  render(){

    return(
      <Dropzone onDrop={this.props.handleOnDrop} accept="image/png" minSize={this.props.minSize} maxSize={this.props.maxSize}>
        {({getRootProps, getInputProps, isDragActive, isDragReject, rejectedFiles}) => {
          const isFileTooLarge = rejectedFiles.length > 0 && rejectedFiles[0].size > this.props.maxSize;
          return (
            <div {...getRootProps()}>
            <MDBView hover onMouseOver={this.handleMouseOver}>
            <input {...getInputProps()}/>
            <AvatarEditor width={this.props.avatarWidth} height={this.props.avatarHeight} image={this.props.image} />
              <MDBMask className="memberMask flex-center" overlay="red-light">
                {!isDragActive && 'Click here or drop a file to upload!'}
                {isDragActive && !isDragReject && "Drop it like it's hot!"}
                {isDragReject && "File type not accepted, sorry!"}
                {isFileTooLarge && (
                  <div className="text-danger mt-2">
                    File is too large.
                  </div>
                )}
              </MDBMask>
            </MDBView>


            </div>
          )}
        }
      </Dropzone>
    );
  }
}
export default DropFile;
