import React from 'react';
import { BrowserRouter, Route, NavLink } from 'react-router-dom'

import HostForm1 from './HostForm1.js';
import HostForm2 from './HostForm2.js';
import HostForm3 from './HostForm3.js';
import HostForm4 from './HostForm4.js';
import ProgressNav from './ProgressNav.js';
import Button from 'react-bootstrap/Button';
import ProgressBar from 'react-bootstrap/ProgressBar';

export class HostForm extends React.Component{
  constructor(props){
    super(props);
    this.state={
      pageNumber:1,
      urlNext:"/BecomeAHost/",
      urlBack:"",
      //FormComponent: "",
      disable:false
    };
    this.handleBack=this.handleBack.bind(this);
    this.handleNext=this.handleNext.bind(this);
  }

  handleBack(){

    if(this.state.pageNumber>1){
      this.setState({
        pageNumber: this.state.pageNumber-1, //if back one more is out of bounds
        urlNext:"/BecomeAHost/"+(this.state.pageNumber-1),
        urlBack:"/BecomeAHost/"+this.state.pageNumber
        //FormComponent:this.state.FormComponent
      });
      console.log(this.state.urlBack);
      console.log(this.state.urlNext);

      console.log(this.state.pageNumber);
    }
  }
  handleNext(){
    if(this.state.pageNumber<4){
      this.setState({
        pageNumber: this.state.pageNumber+1, //if back one more is out of bounds
        urlNext:"/BecomeAHost/"+(this.state.pageNumber+1),
        urlBack:"/BecomeAHost/"+this.state.pageNumber
        //FormComponent:this.state.FormComponent
      });
    }
  }
  render(){
    const FormComponent = this.state.FormComponent;
    var MyComponent = this.state.FormComponent;
    console.log(this.state.urlBack);
    console.log(this.state.urlNext);

    console.log(this.state.pageNumber);
    return(

      <div className="HostForm">
        <div className="HostForm-header">
          <ProgressBar />
        </div>

            <div className="Form-Content">
              <Route path="/BecomeAHost/1" component={HostForm1}/>
              <Route path="/BecomeAHost/2" component={HostForm2}/>
              <Route path="/BecomeAHost/3" component={HostForm3}/>
              <Route path="/BecomeAHost/4" component={HostForm4}/>


            </div>
            <div className="Form-buttons">
            {this.state.pageNumber==1 && <HostForm1/>}
            {this.state.pageNumber==2 && <HostForm2/>}
            {this.state.pageNumber==3 && <HostForm3/>}
            {this.state.pageNumber==4 && <HostForm4/>}
            {this.state.pageNumber>1 && <Button type = "submit" value="Submit" variant="outline-primary" onClick={this.handleBack}><NavLink to={this.state.urlBack}>Back</NavLink></Button> }
            {this.state.pageNumber<4 && <Button type = "submit" value="Submit" variant="outline-primary" onClick={this.handleNext}><NavLink to={this.state.urlNext}>Next</NavLink></Button> }
            </div>
          </div>



    );
  }
}
export default HostForm;
