import React from 'react';
import {MDBInput, MDBContainer,MDBRow, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import MainNav from './MainNav.js';
import 'mdbreact/dist/css/mdb.css'
import { SocialIcon } from 'react-social-icons';
import { Section } from 'react-smart-sections';
import Footer from './Footer.js';
import MeetTeam from './MeetTeam.js';
import HowItWorks from './HowItWorks.js';
import moment from 'moment';
import DayPickerInput from 'react-day-picker/DayPickerInput.js';
import { formatDate, parseDate } from 'react-day-picker/moment';
import 'react-day-picker/lib/style.css';
import '../Styles/FirstPage.css';

export class FirstPage extends React.Component{
  constructor(props){
    super(props);
    this.state={
      value:"",
      nextImg: false,
      startD: '',
      endD:'',
      from: undefined,
      to:undefined
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.changeBackground = this.changeBackground.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleFromChange = this.handleFromChange.bind(this);
    this.handleToChange = this.handleToChange.bind(this);
    this.get2D = this.get2D.bind(this);
    this.formatDates = this.formatDates.bind(this);
    this.handleLearnMore = this.handleLearnMore.bind(this);
  }
  handleChange(event){
    console.log(event.target.value);
    this.setState({value: event.target.value});
  }

  handleSubmit(event){
    event.preventDefault()
    console.log("hi");
    this.props.history.push({
      pathname: '/Listing',
      state: { searchVal: this.state.value }
    });
  }
  //
  changeBackground(){
    console.log("clicked!!");
    this.setState({
      nextImg: !this.state.nextImg
    });

  }
  get2D( num ) {
     if( num.toString().length < 2 ) // Integer of less than two digits
         return "0" + num; // Prepend a zero!
     return num.toString(); // return string for consistency
  }

  formatDates(date){
    const months=['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
    var dateA = date.toString().split(' '); //split by spaceTypes
    let year = dateA[3].toString();
    let day = dateA[2].toString();
    let month = months.indexOf(dateA[1])+1;
    const monthString = this.get2D(month);
    const dayString = this.get2D(day);
    const newDate = year + '-' + monthString + '-' + dayString;
    console.log("new date" + newDate);
    return newDate;
  }

  showFromMonth() {
    const { from, to } = this.state;
    if (!from) {
      return;
    }
    if (moment(to).diff(moment(from), 'months') < 2) {
      this.to.getDayPicker().showMonth(from);
    }
  }
  handleFromChange(from) {
    // Change the from date and focus the "to" input field
    console.log(from);
    const d = this.formatDates(from);
    this.setState({
      from,
      startD: d
    });
  }
  handleToChange(to) {
    console.log(to);
    const d = this.formatDates(to);
    this.setState({
      to,
      endD: d
    }, this.showFromMonth);
  }

  handleLearnMore(){
    console.log("learn more clicked");
  }
  // <div className="firstpage-socialIcons">
  //   <SocialIcon url="https://www.instagram.com/storeopenspace/" style={{ height: 25, width: 25 }} bgColor="#DE1F2B"/>
  //   <SocialIcon url="https://www.linkedin.com/company/openspacestorage/" style={{ height: 25, width: 25 }}/>
  //   <SocialIcon url="https://storeopenspace.com/" style={{ height: 25, width: 25 }}/>
  // </div>

  render(){
    var imgUrl = this.state.nextImg? '/section4-2.png':'/section4.png';
    var divStyle = {
            backgroundImage: 'url(' + imgUrl + ')'
    };
    const { from, to } = this.props;
    const modifiers = { start: from, end: to };

    return(
    <div>

    <link href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-
    awesome.min.css" rel="stylesheet" integrity="sha384-
    wvfXpqpZZVQGK6TAh5PVlGOfQNHSoD2xbE+QkPxCAFlNEevoEH3Sl0sibVcOQVnN"
    crossOrigin="anonymous"/>


  <MainNav showSearch={false}/>

  <div className="firstpage-whole">
    <Section className="section" id="section1">
  <MDBContainer>
  <MDBRow>
  <MDBCol>
    <MDBRow>
      <div>
      Find affordablea nd convenient storage
      </div>
    </MDBRow>
    <form>
    <MDBRow>
      <div className = "firstpage-searchFilter">
        <MDBRow>
          <MDBInput
            label="Where"
            group
            type="text"
          />
        </MDBRow>

        <MDBRow>
          <MDBCol>
            <DayPickerInput
              value={from}
              placeholder="Move-in"
              format="LL"
              formatDate={formatDate}
              parseDate={parseDate}
              dayPickerProps={{
                selectedDays: [from, { from, to }],
                disabledDays: { after: to },
                toMonth: to,
                modifiers,
                numberOfMonths: 1,
                onDayClick: () => this.to.getInput().focus(),
              }}
              onDayChange={this.props.handleFromChange}
            />
          </MDBCol>

          <MDBCol>
            <DayPickerInput
              ref={el => (this.to = el)}
              value={to}
              placeholder="Move-out"
              format="LL"
              formatDate={formatDate}
              parseDate={parseDate}
              dayPickerProps={{
                selectedDays: [from, { from, to }],
                disabledDays: { before: from },
                modifiers,
                month: from,
                fromMonth: from,
                numberOfMonths: 1,
              }}
              onDayChange={this.props.handleToChange}
            />
          </MDBCol>
        </MDBRow>

        <MDBRow>
        <MDBInput
          label="Where"
          group
          type="text"
        />
        </MDBRow>

        <MDBRow>
          <MDBBtn type="submit">Submit</MDBBtn>
        </MDBRow>
      </div>
    </MDBRow>
    </form>
  </MDBCol>

  <MDBCol>
    <MDBRow>
    What We Do
    </MDBRow>

    <MDBRow>
    OpenSpace connects people looking for extra storage to those that have extra space. Think Airbnb, but for storage. Find space that is cheaper, and more convenient than traditional services, and only pay for what you actually store (not an entire unit).
    </MDBRow>
  </MDBCol>
  </MDBRow>
  </MDBContainer>
    </Section>

    <Section className="section" id="section2">
      <MDBContainer>
        <MDBRow>
          <MDBCol>
            <img src="/HowWorksBlue.png" width="320"/>
          </MDBCol>
          <MDBCol>
            <MDBRow>
              <div>
                How It Works
              </div>
            </MDBRow>

            <MDBRow>
              <div>
                <p>OpenSpace is a peer to peer self storage platform that connects college students looking for storage space with people in their area that have it. While also bringing together members of the community, we are focused on offering the most affordable and accessible option in the storage industry.</p>
              </div>
            </MDBRow>

            <MDBRow>
              <MDBBtn onClick={this.handleLearnMore}>Learn More</MDBBtn>
            </MDBRow>

          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </Section>

    <Section className="section" id="section3">
      <MDBContainer>
        <MDBRow>
          <MDBCol>
            What People Are Saying About OpenSpace
          </MDBCol>
          <MDBCol>

          </MDBCol>
        </MDBRow>
      </MDBContainer>
    </Section>

    <Section className="section" id="section4" onClick={this.changeBackground} style={divStyle}>
    </Section>

    <Section className="section" id="section5">
    </Section>

    <Section className="section" id="section6">
      <HowItWorks/>
