import React from 'react';
import Dropdown from 'react-bootstrap/Dropdown';
import { MDBRow, MDBContainer, MDBInput, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import { MDBDropdown, MDBDropdownToggle, MDBDropdownMenu, MDBDropdownItem } from "mdbreact";

import moment from 'moment';
import DayPickerInput from 'react-day-picker/DayPickerInput.js';
import { formatDate, parseDate } from 'react-day-picker/moment';
import 'react-day-picker/lib/style.css';

export class Filter extends React.Component{
  constructor(props){
    super(props);

    this.state={
        value: [],
      from: undefined,
      to: undefined,
      spaceTypes:[
        {
          value:'Restaurant space',
          label:'Restaurant'
        },
        {
          value:'Mall space',
          label:'Mall'
        },
        {
          value:'Apartment space',
          label:'Apartment'
        },
        {
          value:'Garage space',
          label:'Garage'
        },
        {
          value:'Closet space',
          label:'Closet'
        },
        {
          value:'Basement space',
          label:'Basement'
        },
        {
          value:'Outdoor space',
          label:'Outdoor (i.e shed)'
        }
      ]
    }
    this.handleChange = this.handleChange.bind(this);
  }

  showFromMonth() {
    const { from, to } = this.state;
    if (!from) {
      return;
    }
    if (moment(to).diff(moment(from), 'months') < 2) {
      this.to.getDayPicker().showMonth(from);
    }
  }
  handleFromChange(from) {
    // Change the from date and focus the "to" input field
    this.setState({ from });
  }
  handleToChange(to) {
    this.setState({ to }, this.showFromMonth);
  }
  handleChange(event) {

   this.setState(prevState => ({
  value: [...prevState.value, event.target.value]
}));
 }
  handleClick(evt) {
    // Prevent the root click handler from closing the dropdown
    evt.preventDefault();
    evt.nativeEvent.stopImmediatePropagation();
    // Note have to handle anything that needs to happen on "click when closed" in here,
    // not in the wrapped handler.
    if (!this.props.open) {
      this.wrappedClick(evt);
    }
  }
  wrappedClick(evt){
    console.log("hi");
  }

  render(){
    const { from, to } = this.state;
    const modifiers = { start: from, end: to };
    const spaceTypeDropItems = this.state.spaceTypes.map((space, index) =>
        <Dropdown.Item value={space.value}>{space.label}</Dropdown.Item>


    );
    return(
      <div className="filter-main">
        <div className="filter-spaceTypeDropdown">
        <label>
          Pick your favorite flavor:
          <select value={this.state.value} multiple={true} onChange={this.handleChange}>
            <option value="grapefruit">Grapefruit</option>
            <option value="lime">Lime</option>
            <option value="coconut">Coconut</option>
            <option value="mango">Mango</option>
          </select>
        </label>
        </div>
        <div className="InputFromTo">
           <DayPickerInput
             value={from}
             placeholder="From"
             format="LL"
             formatDate={formatDate}
             parseDate={parseDate}
             dayPickerProps={{
               selectedDays: [from, { from, to }],
               disabledDays: { after: to },
               toMonth: to,
               modifiers,
               numberOfMonths: 1,
               onDayClick: () => this.to.getInput().focus(),
             }}
             onDayChange={this.handleFromChange}
           />{' '}
           —{' '}
           <span className="InputFromTo-to">
             <DayPickerInput
               ref={el => (this.to = el)}
               value={to}
               placeholder="To"
               format="LL"
               formatDate={formatDate}
               parseDate={parseDate}
               dayPickerProps={{
                 selectedDays: [from, { from, to }],
                 disabledDays: { before: from },
                 modifiers,
                 month: from,
                 fromMonth: from,
                 numberOfMonths: 1,
               }}
               onDayChange={this.handleToChange}
             />
           </span>
        </div>

      </div>

    );
  }

}

export default Filter;
