import React from 'react';
import MainNav from './MainNav.js';
import Footer from './Footer.js';
import AvatarEditor from 'react-avatar-editor';
import Dropzone from 'react-dropzone';

import { MDBRow, MDBCol, MDBInput, MDBContainer, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import '../Styles/UserProfile.css';
export class UserProfile extends React.Component{
  editor: AvatarEditor;

  constructor(props){
    super(props);
    this.state={
      firstName:'',
      lastName:'',
      email:'',
      phone:0,
      interestRadio:1,
      additionalInfo: '',
      image: 'https://upload.wikimedia.org/wikipedia/commons/4/4b/What_Is_URL.jpg'
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);


  }
  // <Dropzone onDrop={this.handleDrop} disableClick style={{ width: '250px', height: '250px' }}>

  handleChange(event, type){
    console.log("type is "+ type);
    console.log("value is "+ event.target.value);
    this.setState({
      [type]: event.target.value
    })
  }
  onClick = (nr) => () => {
    this.setState({
        interestRadio: nr
    });
  }
  onClickSave = () => {
    if (this.editor) {
      // This returns a HTMLCanvasElement, it can be made into a data URL or a blob,
      // drawn on another canvas, or added to the DOM.
      const canvas = this.editor.getImage()

      // If you want the image resized to the canvas size (also a HTMLCanvasElement)
      const canvasScaled = this.editor.getImageScaledToCanvas()
    }
  }
  onDrop = (acceptedFiles) => {
      console.log(acceptedFiles);
    }
  setEditorRef = (editor: any) => {
     if (editor) {
        this.editor = editor;
        const img = this.editor.getImageScaledToCanvas().toDataURL();
        console.log(img);
    }
}

  handleSubmit(){
    console.log("submit/save profile!");
  }
  onDrop = (files) => {
    // POST to a test endpoint for demo purposes
    console.log("hi");
  }


  render(){
    // const canvas = this.editor.getImage().toDataURL();
    // let imageURL;
    // fetch(canvas)
    //   .then(res => res.blob())
    //   .then(blob => (imageURL = window.URL.createObjectURL(blob)));

// Usage
// <img src={imageURL} ... />
    return(
      <div className="userprofile-main">

        <MainNav/>

        <div className = "userprofile-content">
          <div className="userprofile-title">
            <h1>My OpenSpace Profile</h1>
          </div>
          <MDBContainer>
            <form onSubmit={this.handleSubmit}>
              <MDBRow>
                <MDBCol>
                  <MDBInput
                    onChange={(event)=>this.handleChange(event, 'firstName')}
                    label="First"
                    group
                    type="text"
                    validate
                    required
                  />
                </MDBCol>
                <MDBCol>
                  <MDBInput
                    onChange={(event)=>this.handleChange(event, 'lastName')}
                    label="Last"
                    group
                    type="text"
                    validate
                    required
                  />
                </MDBCol>
              </MDBRow>

              <MDBRow>
                <MDBCol>
                  <MDBInput
                    onChange={(event)=>this.handleChange(event, 'email')}
                    label="Email *"
                    group
                    type="email"
                    validate
                    required
                  />
                </MDBCol>
              </MDBRow>

              <MDBRow>
                <MDBCol>
                  <MDBInput
                    onChange={(event)=>this.handleChange(event, 'phone')}
                    label="Phone *"
                    group
                    type="number"
                    validate
                    required
                  />
                </MDBCol>
              </MDBRow>


              <div className="userprofile-profilepic">
                  <MDBRow>
                      <MDBCol>
                        <h4>Profile Picture</h4>
                      </MDBCol>
                  </MDBRow>
                  <MDBRow>
                      <MDBCol>
                      <Dropzone onDrop={this.onDrop}>
          {({getRootProps, getInputProps}) => (
            <div {...getRootProps()}>
              <input {...getInputProps()} />
              Click me to upload a file!
            </div>
          )}
        </Dropzone>
                          <AvatarEditor     ref={(ref) => this.setEditorRef(ref)}
 width={250} height={250} image={this.state.image} />

                      </MDBCol>
                  </MDBRow>
              </div>

              <div className="userprofile-selector">
              <MDBRow><MDBCol><h4>Are you interested in storing stuff, listing a space, or both?</h4></MDBCol></MDBRow>
                <MDBRow>
                  <MDBCol>
                    <MDBInput gap onClick={this.onClick(1)} checked={this.state.interestRadio===1 ? true : false} label="Storing stuff" type="radio"
                      id="radio1" />
                  </MDBCol>
                </MDBRow>

                <MDBRow>
                  <MDBCol>
                    <MDBInput gap onClick={this.onClick(2)} checked={this.state.interestRadio===2 ? true : false} label="Listing a space" type="radio"
                      id="radio2" />
                  </MDBCol>
                </MDBRow>

                <MDBRow>
                  <MDBCol>
                    <MDBInput gap onClick={this.onClick(3)} checked={this.state.interestRadio===3 ? true : false} label="Both" type="radio"
                      id="radio3"/>
                  </MDBCol>
                </MDBRow>
              </div>
              <div className="userprofile-bio">
                <MDBRow>
                  <MDBCol>
                    <h4>Tell us a little about yourself! (optinal)</h4>
                  </MDBCol>
                </MDBRow>

                <MDBRow>
                  <MDBCol>
                    <MDBInput onChange={(event)=>this.handleChange(event, 'additionalInfo')} id="bookform-extrainfo" type="textarea" label="ex: I am student at ... " rows="5" cols="100"/>
                  </MDBCol>
                </MDBRow>
              </div>

              <MDBRow>
                <MDBCol>
                  <MDBBtn type="submit">Save!</MDBBtn>
                </MDBCol>
              </MDBRow>
            </form>
          </MDBContainer>
        </div>


        <footer className ="userprofile-footer">
            <Footer/>
        </footer>
      </div>
    );
  }
}
export default UserProfile;
