import React from 'react';
import { MDBContainer,MDBRow, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import MainNav from './MainNav.js';
import 'mdbreact/dist/css/mdb.css'
import { SocialIcon } from 'react-social-icons';
import { Section } from 'react-smart-sections';
import Footer from './Footer.js';
import MeetTeam from './MeetTeam.js';
import HowItWorks from './HowItWorks.js';
import '../Styles/FirstPage.css';

export class FirstPage extends React.Component{
  constructor(props){
    super(props);
    this.state={
      value:"",
      nextImg: false
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.changeBackground = this.changeBackground.bind(this);
  }
  handleChange(event){
    console.log(event.target.value);
    this.setState({value: event.target.value});
  }

  handleSubmit(event){
    event.preventDefault()
    console.log("hi");
    this.props.history.push({
      pathname: '/Listing',
      state: { searchVal: this.state.value }
    });
  }
  //
  changeBackground(){
    console.log("clicked!!");
    this.setState({
      nextImg: !this.state.nextImg
    });

  }


  render(){
    var imgUrl = this.state.nextImg? '/section4-2.png':'/section4.png';
    var divStyle = {
            backgroundImage: 'url(' + imgUrl + ')'
    };
    return(
<div>

<link href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-
awesome.min.css" rel="stylesheet" integrity="sha384-
wvfXpqpZZVQGK6TAh5PVlGOfQNHSoD2xbE+QkPxCAFlNEevoEH3Sl0sibVcOQVnN"
crossOrigin="anonymous"/>


  <MainNav showSearch={false}/>

<div className="firstpage-whole">
  <Section className="section" id="section1">
<MDBContainer>
<MDBRow>
<MDBCol>
  <MDBRow>
    <div>
    Find affordablea nd convenient storage
    </div>
  </MDBRow>

  <MDBRow>
    <div className = "firstpage-searchFilter">
      <MDBRow>
        <button>hi</button>
      </MDBRow>

      <MDBRow>
        <MDBCol>
          <DayPickerInput
            value={from}
            placeholder="From"
            format="LL"
            formatDate={formatDate}
            parseDate={parseDate}
            dayPickerProps={{
              selectedDays: [from, { from, to }],
              disabledDays: { after: to },
              toMonth: to,
              modifiers,
              numberOfMonths: 1,
              onDayClick: () => this.to.getInput().focus(),
            }}
            onDayChange={this.props.handleFromChange}
          />
        </MDBCol>

        <MDBCol>
          <DayPickerInput
            ref={el => (this.to = el)}
            value={to}
            placeholder="To"
            format="LL"
            formatDate={formatDate}
            parseDate={parseDate}
            dayPickerProps={{
              selectedDays: [from, { from, to }],
              disabledDays: { before: from },
              modifiers,
              month: from,
              fromMonth: from,
              numberOfMonths: 1,
            }}
            onDayChange={this.props.handleToChange}
          />
        </MDBCol>
      </MDBRow>

      <MDBRow>
        <button></button>
      </MDBRow>

      <MDBRow>
        <button></button>
      </MDBRow>
    </div>
  </MDBRow>
</MDBCol>

<MDBCol>
  <MDBRow>
  What We Do
  </MDBRow>

  <MDBRow>
  OpenSpace connects people looking for extra storage to those that have extra space. Think Airbnb, but for storage. Find space that is cheaper, and more convenient than traditional services, and only pay for what you actually store (not an entire unit).
  </MDBRow>
</MDBCol>
</MDBRow>
</MDBContainer>
    <div className="firstpage-search">
      <MDBCol md="6" id="searchCol">
        <MDBFormInline className="md-form" onSubmit={this.handleSubmit}>
          <input id="firstpage-input" className="form-control form-control-sm w-75" type="text" placeholder="Search" aria-label="Search" onChange={this.handleChange}/>
        </MDBFormInline>
      </MDBCol>
    </div>


    <div className="firstpage-socialIcons">
      <SocialIcon url="https://www.instagram.com/storeopenspace/" style={{ height: 25, width: 25 }} bgColor="#DE1F2B"/>
      <SocialIcon url="https://www.linkedin.com/company/openspacestorage/" style={{ height: 25, width: 25 }}/>
      <SocialIcon url="https://storeopenspace.com/" style={{ height: 25, width: 25 }}/>
    </div>
  </Section>

  <Section className="section" id="section2">
  </Section>

  <Section className="section" id="section3">
  </Section>

  <Section className="section" id="section4" onClick={this.changeBackground} style={divStyle}>
  </Section>

  <Section className="section" id="section5">
  </Section>

  <Section className="section" id="section6">
    <HowItWorks/>
  </Section>








  </div>

  <footer className ="firstpage-footer">
      <Footer/>
  </footer>

</div>

    );
  }


}
export default FirstPage;
