import React from 'react';
import { MDBRow, MDBContainer, MDBInput, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";



export class CountItem extends React.Component{
  constructor(props){
    super(props);
    this.state={
      count:0
    };
  }

  handleIncrement(){
    console.log("up");
    this.setState({
      count:this.state.count+1
    });
  }
  handleDecrement(){
    console.log("down");
    this.setState({
      count:this.state.count-1
    });
  }
  render(){
    return(
      <div>
      <MDBRow>
        <MDBCol>
          {this.props.name}
        </MDBCol>
        <MDBCol>
          <MDBCol><MDBBtn onClick={this.handleIncrement}>up</MDBBtn></MDBCol>
          <MDBCol>{this.state.count}</MDBCol>
          <MDBCol><MDBBtn onClick={this.handleDecrement}>down</MDBBtn></MDBCol>
        </MDBCol>
      </MDBRow>
      </div>

    );
  }
}
export default CountItem;
