import React from 'react';
import { MDBRow, MDBCol, MDBInput, MDBContainer, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";

export class UserProfile extends React.Component{
  constructor(props){
    super(props);
    this.state={
      firstName:'',
      lastName:'',
      email:'',
      phone:0,
      interestRadio:1,
      additionalInfo: ''
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }
  handleChange(event, type){
    console.log("type is "+ type);
    console.log("value is "+ event.target.value);
    this.setState({
      [type]: event.target.value
    })
  }
  onClick = (nr) => () => {
    this.setState({
        interestRadio: nr
    });
  }
  handleSubmit(){
    console.log("submit/save profile!");
  }


  render(){
    return(
      <div className="userprofile-main">
      <MDBContainer>
      <form onSubmit={this.handleSubmit}>
      <MDBRow>
        <MDBCol>
          <MDBInput
            onChange={(event)=>this.handleChange(event, 'firstName')}
            label="First"
            group
            type="text"
            validate
            required
          />
        </MDBCol>
        <MDBCol>
          <MDBInput
            onChange={(event)=>this.handleChange(event, 'lastName')}
            label="Last"
            group
            type="text"
            validate
            required
          />
        </MDBCol>
      </MDBRow>

      <MDBRow>
        <MDBCol>
          <MDBInput
            onChange={(event)=>this.handleChange(event, 'email')}
            label="Email *"
            group
            type="email"
            validate
            required
          />
        </MDBCol>
      </MDBRow>

      <MDBRow>
        <MDBCol>
          <MDBInput
            onChange={(event)=>this.handleChange(event, 'phone')}
            label="Phone *"
            group
            type="number"
            validate
            required
          />
        </MDBCol>
      </MDBRow>

      <MDBRow>
        <MDBCol>
          <h1>hi</h1>
        </MDBCol>
      </MDBRow>

      <MDBRow>
          <MDBRow>
            <MDBInput gap onClick={this.onClick(1)} checked={this.state.interestRadio===1 ? true : false} label="Storing stuff" type="radio"
            id="radio1" />
          </MDBRow>

          <MDBRow>
            <MDBInput gap onClick={this.onClick(2)} checked={this.state.interestRadio===2 ? true : false} label="Listing a space" type="radio"
            id="radio2" />
          </MDBRow>

          <MDBRow>
            <MDBInput gap onClick={this.onClick(3)} checked={this.state.interestRadio===3 ? true : false} label="Both" type="radio"
            id="radio3"/>
          </MDBRow>
      </MDBRow>


      <MDBRow>
        <MDBInput onChange={(event)=>this.handleChange(event, 'additionalInfo')} id="bookform-extrainfo" type="textarea" label="ex: I have a oddly shaped item..." rows="5" cols="100"/>
      </MDBRow>

      <MDBRow>
        <MDBBtn type="submit">Save</MDBBtn>
      </MDBRow>
      </form>
      </MDBContainer>
      </div>
    );
  }
}
export default UserProfile;
