import React from 'react';
import { MDBRow, MDBContainer, MDBInput, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import '../Styles/CounterItem.css';


export class CountItem extends React.Component{
  constructor(props){
    super(props);
    // this.state={
