import React from 'react';
import { MDBRow, MDBContainer, MDBInput, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";
import { AutoComplete, ComboBox, DropDownList, MultiSelect } from '@progress/kendo-react-dropdowns';

export class BookForm extends React.Component{
  constructor(props){
    super(props);
    this.state={
      contactRadio:1,
      deliveryRadio:1,
      showBox:false,
      showOther:false,
      showBike: false,
      showLargeBox:false,
      showSmallFurniture:false,
      showLargeFurniture:false
    };
  }

  onClick = (nr, type) => () => {
    console.log(type);
    console.log(nr);
    let typeRadio = type;
    this.setState({
      [type]: nr
    });
  }

  handleValueSelected=(event) =>{
    noSpace = event.target.value.split(' ').join('');
    let storeItem = `show${noSpace}`;
    this.setState({
      [storeItem]:true
    });
  }

  render(){
    const storageItemTypes = ["Box, Bike, Large Box, Small Furniture, Large Furniture, Other"];
    return(
      <div className="bookform-main">
        <MDBContainer>
          <MDBRow>
            <h1>Book this Space</h1>
            <h4>Please fill out the form below to submit a booking request. We will follow up as soon as possible to confirm the booking and setup details for payment, delivery, etc.</h4>
          </MDBRow>

          <MDBRow>
            <form>
              <MDBRow>
                <MDBInput
                  label="Your name *"
                  group
                  type="text"
                  validate
                  error="wrong"
                  success="right"
                />
              </MDBRow>
              <MDBRow>
                <MDBCol><h3> Preferred form of contact: </h3></MDBCol>
                <MDBCol><MDBInput gap onClick={this.onClick(1, "contactRadio")} checked={this.state.contactRadio===1 ? true : false} label="email" type="radio"
                id="radio1" /></MDBCol>
                <MDBCol><MDBInput gap onClick={this.onClick(2, "contactRadio")} checked={this.state.contactRadio===2 ? true : false} label="phone" type="radio"
                id="radio2" /></MDBCol>
                {this.state.contactRadio===1 && <MDBInput
                  label="Your email *"
                  group
                  type="email"
                  validate
                  error="wrong"
                  success="right"
                />}

                {this.state.contactRadio===2 && <MDBInput
                  label="Your phone *"
                  group
                  type="number"
                  validate
                  error="wrong"
                  success="right"
                />}
              </MDBRow>
              <MDBRow>
                <MDBCol><h3> interested in delivery?: </h3></MDBCol>
                <MDBCol><MDBInput gap onClick={this.onClick(1, "deliveryRadio")} checked={this.state.deliveryRadio===1 ? true : false} label="yes" type="radio"
                id="radio1" /></MDBCol>
                <MDBCol><MDBInput gap onClick={this.onClick(2, "deliveryRadio")} checked={this.state.deliveryRadio===2 ? true : false} label="no" type="radio"
                id="radio2" /></MDBCol>
              </MDBRow>
              <MDBRow>
                <h1>dates you need your items stored</h1>
              </MDBRow>
              <MDRow>
              <MultiSelect
                      data={storageItemTypes}
                      onChange={this.onChange}
                      value={this.state.value}
                  />
              </MDRow>




            </form>
          </MDBRow>
        </MDBContainer>
      </div>
    );
  }
}
export default BookForm;
