import React from 'react'
import {compose, withProps} from "recompose"
import {GoogleMap, Marker, withGoogleMap, withScriptjs, InfoWindow} from 'react-google-maps'
import MapMarker from "./MapMarker";

export class Map extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
      const bounds  = new google.maps.LatLngBounds();
      const loc = new google.maps.LatLng(marker.position.lat(), marker.position.lng());
      bounds.extend(loc);
        return (
            this.props.data &&
            <GoogleMap
                defaultZoom={12}
                defaultCenter={{lat: 36.0014, lng: -78.9382}}
                fitBounds = {bounds}
                panToBounds = {bounds}
            >
                {this.props.data.map(listing => <MapMarker key={listing.id} listing={listing}/>)}
            </GoogleMap>
        )
    }
}

export default compose(
    withProps({
        googleMapURL: "https://maps.googleapis.com/maps/api/js?key=AIzaSyDbGXFv-QOejj2G8vfGj5cIuYqXjI1AhRU&",
        loadingElement: <div style={{height: `100%`}}/>,
        containerElement: <div style={{height: `100vh`}}/>,
        mapElement: <div style={{height: `100%`}}/>,
    }),
    withScriptjs,
    withGoogleMap
)((Map))
