import React from 'react';
import { MDBRow, MDBContainer, MDBInput, MDBCol, MDBFormInline, MDBIcon, MDBBtn } from "mdbreact";


export class BookForm extends React.Component{
  constructor(props){
    super(props);
    this.state={
      radio:1,
      showContact:false
    };
  }

  onClick = (nr) => () => {
    this.setState({
      radio: nr,
      showContact:true
    });
  }
  render(){
    return(
      <div className="bookform-main">
        <MDBContainer>
          <MDBRow>
            <h1>Book this Space</h1>
            <h4>Please fill out the form below to submit a booking request. We will follow up then as soon as possible to confirm the booking and setup details for payment, delivery, etc.</h4>
          </MDBRow>

          <MDBRow>
            <form>
              <MDBRow>
                <MDBInput
                  label="Your name *"
                  group
                  type="text"
                  validate
                  error="wrong"
                  success="right"
                />
              </MDBRow>
              <MDBRow>
                <h3> Preferred form of contact: </h3>
                <MDBInput gap onClick={this.onClick(1)} checked={this.state.radio===1 ? true : false} label="email" type="radio"
                id="radio1" />
                <MDBInput gap onClick={this.onClick(2)} checked={this.state.radio===2 ? true : false} label="phone" type="radio"
                id="radio2" />
                {this.state.radio===1 && this.state.showContact && <MDBInput
                  label="Your email *"
                  group
                  type="email"
                  validate
                  error="wrong"
                  success="right"
                />}

                {this.state.radio===2 && this.state.showContact && <MDBInput
                  label="Your phone *"
                  group
                  type="number"
                  validate
                  error="wrong"
                  success="right"
                />}
              </MDBRow>


            </form>
          </MDBRow>
        </MDBContainer>
      </div>
    );
  }
}
export default BookForm;
