import React from 'react';
import { MDBCol, MDBFormInline, MDBIcon } from "mdbreact";
import MainNav from './MainNav.js';


export class FirstPage extends React.Component{
  constructor(props){
    super(props);
    this.state={
      value:""
    };
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event){
    console.log(event.target.value);
    this.setState({value: event.target.value});
  }

  handleSubmit(){
    this.props.history.push({
      pathname: '/Listing',
      state: { searchVal: this.state.value }
    });
  }


  render(){
    let data = {pathname: '/details', name: this.props.name, releaseDate:this.props.releaseDate, rating: this.props.rating, channel:this.props.channel }
    return(
//MAIN NAV
<div>
<header className="firstpage-header">
  <MainNav/>
</header>

//search bar
    <MDBCol md="6">
      <MDBFormInline className="md-form">
        <MDBIcon icon="search" />
        <input className="form-control form-control-sm ml-3 w-75" type="submit" placeholder="Search" aria-label="Search" onChange={this.handleChange} onSubmit={this.handleSubmit}/>
      </MDBFormInline>
    </MDBCol>
</div>

    );
  }


}
export default FirstPage;
